function Cache(logger){
  this._cache = {};
  this.logger = logger;
}
Cache.prototype.save = function(option, result){
  return this._cache[option.cacheKey] = result;
}
Cache.prototype.check = function(option){
  var result;
  if (!option.cacheKey) return false;
  option.cacheResult = this._cache[option.cacheKey];
  if (option.cacheResult === undefined) return false;
  this.logger && this.logger('cache hit %j', option);
  return true;
}

exports.class = Cache;