/**
 * execute a batch within max acts count limit,
 * then throttle new act
 * can clear the batch by call .clear,
 * that allow new batch to continue run
 * @param leastAmount
 * @constructor
 */
function BatchExec(leastAmount){
  this.leastAmount = leastAmount;
  this.count = 0;
}
BatchExec.prototype.check = function(option){
  return this.count >= this.leastAmount;
}
BatchExec.prototype.count = function(option){
  this.count++;
}
BatchExec.prototype.actcb = function(option){
  return true;
}
BatchExec.prototype.clear = function(option){
  this.count = 0;
  // call resume
}

exports.class = BatchExec;