/**
 * execute a batch within max acts count limit,
 * then throttle new act
 * can clear the batch by call .clear,
 * that allow new batch to continue run
 * @param maxExec
 * @constructor
 */
function Batcher(maxExec){
  this.maxExec = maxExec;
  this.count = 0;
}
Batcher.prototype.check = function(option){
  return this.count <= this.maxExec;
}
Batcher.prototype.count = function(option){
  this.count++;
}
Batcher.prototype.actcb = function(option){
  return true;
}
Batcher.prototype.clear = function(option){
  this.count = 0;
  // call resume
}

exports.class = Batcher;