function ConcurrencyLimiter(maxConcurrency){
  this.maxConcurrency = maxConcurrency;
  this.onWayCount = 0;
}
ConcurrencyLimiter.prototype.check = function(option){
  return this.onWayCount < this.maxConcurrency;
}
ConcurrencyLimiter.prototype.count = function(option){
  this.onWayCount++;
}
ConcurrencyLimiter.prototype.actcb = function(option){
  this.onWayCount--;
  return true;
}

exports.class = ConcurrencyLimiter;