function OneByOne(interval, absolute){
  this.interval = interval || 3000;
  this.absolute = absolute || false;
  this.suspend = false;
}
OneByOne.prototype.check = function(){
  return !this.suspend;
}
OneByOne.prototype.count = function(){
  this.suspend = true;
  if (this.absolute) {
    var me = this;
    setTimeout(function(){
      me.suspend = false;
      me._resume();
    }, me.interval);
  }
}
OneByOne.prototype.actcb = function(){
  if (!this.absolute) {
    var me = this;
    setTimeout(function(){
      me.suspend = false;
      me._resume();
    }, me.interval);
  }
  return false;
}

exports.class = OneByOne;