/**
 * allow bare manual forced pause/resume for SuperQueue
 * @constructor
 */
function Pauser(){
  this.isPaused = false;
}
Pauser.prototype.check = function(option){
  return !this.isPaused;
}
Pauser.prototype.pause = function(option){
  this.isPaused = true;
}
Pauser.prototype.suspend = function(option){
  this.isPaused = true;
}
Pauser.prototype.resume = function(option){
  this.isPaused = false;
  this._resume();
}

exports.class = Pauser;