function ResultArray(){
  this.count = 0;
  this.results = [];
}
ResultArray.prototype.count = function(option){
  option = option || {};
  option.order = this.count++;
  this.results.push(undefined);
}
ResultArray.prototype.actcb = function(err, result, option){
  this.results[option.order] = result;
}

exports.class = ResultArray;