function Tracer(logger, prop){
  this.logger = logger || console.log;
  this.prop = prop || 'desc';
}
Tracer.prototype.count = function(option){
  this.logger('begin %j', option[this.prop] || option);
}
Tracer.prototype.actcb = function(err, result, option){
  this.logger('end %j', option[this.prop] || option);
  return true;
}
exports.class = Tracer;