function db(){
  for (var i = 1; i < 255; i++) {
    (function(i){
      var ip = '192.168.1.' + i;
      // console.log(ip);
      scanPorts(ip, false, 8001, 8001, {
        logStepCount : 100,
        namedPorts : {},
        exclude : /(refuse|forbid)/
      });
      scanPorts(ip, false, 22036, 22036, {
        logStepCount : 100,
        namedPorts : {},
        exclude : /(refuse|forbid)/
      });
    })(i);

  }
}
